    /**
     * List<DetailModel>,DetailModel的验证
     */
    private void checkListTypeModel(String type, Object obj, String fieldName
            , JsonObject jsonObject, String sName) throws Exception {
        //model中的List<DetailModel>,DetailModel的验证
        if (type.contains("java.util.List")) {   //如果type是类类型，则前面包含"class "，后面跟类名
            Method m = obj.getClass().getMethod("get" + fieldName);
            List<Object> value = (List) m.invoke(obj);    //调用getter方法获取属性值
            //获取list中具体的Model类型中的所有字段名（json）
            JsonArray jsonArray = jsonObject.getAsJsonArray(sName);
            Set<String> setArray = null;
            if (jsonArray == null || jsonArray.size() == 0) {
                return;
            }
            if (value == null || value.size() == 0) {
                return;
            }
            //json中的
            setArray = jsonArray.get(0).getAsJsonObject().keySet();
            //model中的
            List<String> innerList = new ArrayList<>();
            Object fObj = value.get(0);
            Field[] innerField = fObj.getClass().getDeclaredFields();
            String innerClassName = fObj.getClass().getSimpleName();
            //System.out.println("===========className:   "+innerClassName);
            for (int i = 0; i < innerField.length; i++) {  //遍历所有属性
                String innerFieldName = innerField[i].getName();    //获取属性的名字
                String innerType = innerField[i].getType().toString(); //获取属性的类型
                String innerSeriableName = getSeriableValue(innerClassName, innerFieldName);

                String innerFieldNameWrapper = innerFieldName.substring(0, 1).toUpperCase() + innerFieldName.substring(1);
                checkListTypeModel(innerType, fObj, innerFieldNameWrapper, jsonArray.get(0).getAsJsonObject(), innerSeriableName);

                //model中具体的自定义的Model
                checkDetailModel(innerType, fObj, innerFieldNameWrapper, jsonArray.get(0).getAsJsonObject(), innerSeriableName);

                innerList.add(innerSeriableName);
            }
            //对比json中list中model的所有字段和本地Model类中的字段
            diffAllField(setArray, innerList, fObj.getClass().getSimpleName());
        }
    }


    public void ReflectTest(Object objSet, Set<String> keySet, JsonObject totalJsonObject) throws Exception {
        Field[] field = objSet.getClass().getDeclaredFields();  //获取当前实体类的所有属性，返回Field数组
        outList = new ArrayList<>();
        for (int j = 0; j < field.length; j++) {
            //遍历所有属性,获取属性的名字
            String name = field[j].getName();
            //将属性的首字符大写，方便构造get，set方法
            name = name.substring(0, 1).toUpperCase() + name.substring(1);
            String type = field[j].getType().toString();    //获取属性的类型
            String seriableName = getSeriableValue(objSet.getClass().getSimpleName(), field[j].getName());
            System.out.println("========" + type);
            outList.add(seriableName);
            //model中的List<DetailModel>,DetailModel的验证
            checkListTypeModel(type, objSet, name, totalJsonObject, seriableName);
            //model中具体的自定义的Model
            checkDetailModel(type, objSet, name, totalJsonObject, seriableName);

        }
        diffAllField(keySet, outList, objSet.getClass().getSimpleName());
    }

    private void checkDetailModel(String type, Object obj, String fieldName
            , JsonObject jbject, String sName) throws Exception {
        if (fieldName.equals("Companion")) {
            return;
        }
        //表示具体的自定义的Model
        boolean containsDetailModel = !(type.contains("java.lang.Integer")
                || type.contains("java.lang.String")
                || type.contains("java.lang.Boolean")
                || type.contains("java.util.List")
                || type.contains("java.lang.Float")
                || type.contains("java.lang.Double")
                || type.contains("java.lang.Long"))
                && type.contains("Model");
        if (containsDetailModel) {
            Method m = obj.getClass().getMethod("get" + fieldName);
            Object innerObj = m.invoke(obj);
            if (innerObj == null) {
                return;
            }
            Field[] fields = innerObj.getClass().getDeclaredFields();
            //model类中的所有@SeriableName中括号的字段
            List<String> list = new ArrayList<>();
            for (int i = 0; i < fields.length; i++) {
                String innerFieldName = fields[i].getName();    //获取属性的名字
                //System.out.println("========="+innerFieldName+"     "+innerObj.getClass().getSimpleName());
                boolean isAllCaps = testAllUpperCase(innerFieldName.replace("_", "")) || "Companion".equals(innerFieldName);
                if (isAllCaps) {
                    continue;
                }
                String innerSeriableName = getSeriableValue(innerObj.getClass().getSimpleName(), innerFieldName);
                list.add(innerSeriableName);
            }
            JsonObject jsonObject = jbject.getAsJsonObject(sName);
            if (jsonObject == null) {
                return;
            }
            Set<String> innerSet = jsonObject.keySet();
            diffAllField(innerSet, list, innerObj.getClass().getSimpleName());
        }
    }


    //对比字段
    public void diffAllField(Set<String> jsonSet, List<String> localModelFieldList, String className) {

        //校验本地的model类中是否有json字符串中的所有key
        Iterator<String> iterable = jsonSet.iterator(); //json字符串中的
        int value = 0;
        while (iterable.hasNext()) {
            String fieldName = iterable.next();
            boolean hasExistValue = false;
            for (int i = 0; i < localModelFieldList.size(); i++) {
                if (localModelFieldList.get(i).equals(fieldName)) {
                    value++;
                    hasExistValue = true;
                }
            }
            if (!hasExistValue) {
                System.out.println("===========json中无法解析的字段 :    " + fieldName + "    类名：" + className);
            }
        }
        if (value == jsonSet.size()) {
            //System.out.println("===========:                    All field has checked");
        } else {
            System.out.println("===========:                Not all field has checked");
        }

    }


    //获取字段的@SerializedName中括号内的值
    public static String getSeriableValue(String className, String fieldName) throws Exception {
        Field field = getFieldByStringName(className, fieldName);
        if (field == null) {
            return "";
        }
        SerializedName ss = field.getAnnotation(SerializedName.class);
        if (ss == null) {
            return "";
        }
        return ss.value();
    }

